    <ProductList>
      {products.map((product) => (
        <li key={product.id}>
          <img
            src="https://rocketseat-cdn.s3-sa-east-1.amazonaws.com/modulo-redux/tenis1.jpg"
            alt="Tênis de Caminhada Leve Confortável"
          />
          <strong>Tênis de Caminhada Leve Confortável</strong>
          <span>R$ 179,90</span>
          <button
            type="button"
